// Type definitions for Electron API exposed via preload script

export interface TranscriptionProgress {
  progress: number;
  status: 'loading' | 'transcribing' | 'completed' | 'error';
  message: string;
}

export interface TranscriptionResult {
  success: boolean;
  text?: string;
  duration?: number;
  error?: string;
}

export interface SaveResult {
  success: boolean;
  filePath?: string;
  error?: string;
}

export interface Settings {
  theme: 'light' | 'dark' | 'system';
  language: string;
  autoSave: boolean;
  outputDirectory: string;
}

export interface AudioDevice {
  id: string;
  label: string;
  kind: 'audioinput' | 'audiooutput';
}

// Global settings for the speech-to-text feature
export interface GlobalSettings {
  hotkey: string;
  sttProvider: 'deepgram' | 'whisper';
  preserveClipboard: boolean;
  showOverlay: boolean;
  overlayPosition: 'top-right' | 'top-left' | 'bottom-right' | 'bottom-left';
}

// Permission status types
export type PermissionStatus = 'granted' | 'denied' | 'not-determined' | 'restricted';

export interface PermissionState {
  microphone: PermissionStatus;
  accessibility: PermissionStatus;
}

// Overlay window state (Phase 2)
export interface OverlayState {
  isRecording: boolean;
  transcriptPreview?: string;
  duration?: number;
}

// Streaming transcription (Phase 5)
export interface StreamingResult {
  text: string;
  isFinal: boolean;
  confidence?: number;
}

export type TranscriptionMode = 'deepgram' | 'whisper';

export interface ElectronAPI {
  // Platform info
  platform: string;

  // Transcription operations
  startRecording: () => Promise<{ success: boolean; error?: string }>;
  stopRecording: () => Promise<{ success: boolean; audioPath?: string; error?: string }>;
  transcribeFile: (filePath: string) => Promise<{ success: boolean; error?: string }>;
  transcribeAudio: (audioBuffer: ArrayBuffer) => Promise<TranscriptionResult>;
  preloadWhisper: () => Promise<{ success: boolean; error?: string }>;

  // File operations
  saveTranscript: (text: string, fileName?: string) => Promise<SaveResult>;
  loadTranscript: (filePath: string) => Promise<{ success: boolean; text?: string; error?: string }>;
  selectAudioFile: () => Promise<{ filePath?: string; cancelled: boolean }>;
  selectOutputDirectory: () => Promise<{ dirPath?: string; cancelled: boolean }>;

  // Settings
  loadSettings: () => Promise<Settings>;
  saveSettings: (settings: Partial<Settings>) => Promise<{ success: boolean; error?: string }>;

  // Audio devices
  getAudioDevices: () => Promise<AudioDevice[]>;

  // Microphone permissions (macOS)
  checkMicrophonePermission: () => Promise<{ status: string; platform: string }>;
  requestMicrophonePermission: () => Promise<{ granted: boolean }>;

  // Global settings (Phase 1)
  getGlobalSettings: () => Promise<GlobalSettings & { hasApiKey: boolean }>;
  saveGlobalSettings: (settings: Partial<GlobalSettings>) => Promise<{ success: boolean; error?: string }>;
  saveApiKey: (apiKey: string) => Promise<{ success: boolean; error?: string }>;
  hasApiKey: () => Promise<boolean>;
  clearApiKey: () => Promise<{ success: boolean }>;

  // Permissions (Phase 1)
  getAllPermissions: () => Promise<PermissionState>;
  checkAccessibilityPermission: () => Promise<{ status: PermissionStatus }>;
  requestAccessibilityPermission: () => Promise<{ granted: boolean }>;
  openAccessibilityPreferences: () => Promise<void>;
  openMicrophonePreferences: () => Promise<void>;

  // Global Hotkeys (Phase 3)
  registerGlobalHotkey: (accelerator: string) => Promise<{ success: boolean; error?: string }>;
  unregisterGlobalHotkey: () => Promise<{ success: boolean }>;
  getCurrentHotkey: () => Promise<string | null>;
  isHotkeyRegistered: () => Promise<boolean>;

  // Text Injection (Phase 4)
  injectText: (text: string, preserveClipboard?: boolean) => Promise<{ success: boolean; error?: string }>;
  copyToClipboard: (text: string) => Promise<{ success: boolean; error?: string }>;
  isTextInjectionAvailable: () => Promise<boolean>;

  // Streaming Transcription (Phase 5)
  startStreamingTranscription: (apiKey?: string) => Promise<{ success: boolean; mode: TranscriptionMode; error?: string }>;
  stopStreamingTranscription: () => Promise<{ transcript: string }>;
  sendAudioChunk: (audioData: ArrayBuffer) => Promise<void>;

  // Event listeners
  onGlobalRecordingStarted: (callback: () => void) => () => void;
  onGlobalRecordingStopped: (callback: () => void) => () => void;
  onTranscriptionProgress: (callback: (progress: TranscriptionProgress) => void) => void;
  onTranscriptionComplete: (callback: (text: string) => void) => void;
  onAudioChunk: (callback: (chunk: ArrayBuffer) => void) => void;
  onOverlayStateChange: (callback: (state: OverlayState) => void) => () => void;
  onStreamingTranscript: (callback: (result: StreamingResult) => void) => () => void;
  onStreamingError: (callback: (error: string) => void) => () => void;
  removeListener: (channel: string) => void;
  removeAllListeners: (channel: string) => void;
}

declare global {
  interface Window {
    electronAPI: ElectronAPI;
  }
}

import { contextBridge, ipcRenderer } from "electron";

// CRITICAL: Only expose specific, type-safe methods
// Never expose generic send/on/invoke to prevent security vulnerabilities
contextBridge.exposeInMainWorld("electronAPI", {
  // Platform info
  platform: process.platform,

  // Transcription operations
  startRecording: () => ipcRenderer.invoke("start-recording"),
  stopRecording: () => ipcRenderer.invoke("stop-recording"),
  transcribeFile: (filePath: string) => ipcRenderer.invoke("transcribe-file", filePath),
  transcribeAudio: (audioBuffer: ArrayBuffer) => ipcRenderer.invoke("transcribe-audio", audioBuffer),
  preloadWhisper: () => ipcRenderer.invoke("preload-whisper"),

  // File operations
  saveTranscript: (text: string, fileName?: string) =>
    ipcRenderer.invoke("save-transcript", text, fileName),
  loadTranscript: (filePath: string) => ipcRenderer.invoke("load-transcript", filePath),
  selectAudioFile: () => ipcRenderer.invoke("select-audio-file"),
  selectOutputDirectory: () => ipcRenderer.invoke("select-output-directory"),

  // Settings
  loadSettings: () => ipcRenderer.invoke("load-settings"),
  saveSettings: (settings: unknown) => ipcRenderer.invoke("save-settings", settings),

  // Audio devices
  getAudioDevices: () => ipcRenderer.invoke("get-audio-devices"),

  // Microphone permissions (macOS)
  checkMicrophonePermission: () => ipcRenderer.invoke("check-microphone-permission"),
  requestMicrophonePermission: () => ipcRenderer.invoke("request-microphone-permission"),

  // Global settings (Phase 1)
  getGlobalSettings: () => ipcRenderer.invoke("get-global-settings"),
  saveGlobalSettings: (settings: unknown) => ipcRenderer.invoke("save-global-settings", settings),
  saveApiKey: (apiKey: string) => ipcRenderer.invoke("save-api-key", apiKey),
  hasApiKey: () => ipcRenderer.invoke("has-api-key"),
  clearApiKey: () => ipcRenderer.invoke("clear-api-key"),

  // Permissions (Phase 1)
  getAllPermissions: () => ipcRenderer.invoke("get-all-permissions"),
  checkAccessibilityPermission: () => ipcRenderer.invoke("check-accessibility-permission"),
  requestAccessibilityPermission: () => ipcRenderer.invoke("request-accessibility-permission"),
  openAccessibilityPreferences: () => ipcRenderer.invoke("open-accessibility-preferences"),
  openMicrophonePreferences: () => ipcRenderer.invoke("open-microphone-preferences"),

  // Global Hotkeys (Phase 3)
  registerGlobalHotkey: (accelerator: string) => ipcRenderer.invoke("register-global-hotkey", accelerator),
  unregisterGlobalHotkey: () => ipcRenderer.invoke("unregister-global-hotkey"),
  getCurrentHotkey: () => ipcRenderer.invoke("get-current-hotkey"),
  isHotkeyRegistered: () => ipcRenderer.invoke("is-hotkey-registered"),

  // Text Injection (Phase 4)
  injectText: (text: string, preserveClipboard?: boolean) =>
    ipcRenderer.invoke("inject-text", text, preserveClipboard),
  copyToClipboard: (text: string) => ipcRenderer.invoke("copy-to-clipboard", text),
  isTextInjectionAvailable: () => ipcRenderer.invoke("is-text-injection-available"),

  // Streaming Transcription (Phase 5)
  startStreamingTranscription: (apiKey?: string) =>
    ipcRenderer.invoke("start-streaming-transcription", apiKey),
  stopStreamingTranscription: () => ipcRenderer.invoke("stop-streaming-transcription"),
  sendAudioChunk: (audioData: ArrayBuffer) => ipcRenderer.invoke("send-audio-chunk", audioData),

  // Event listeners for streaming updates
  onGlobalRecordingStarted: (callback: () => void) => {
    const listener = () => callback();
    ipcRenderer.on("global-recording-started", listener);
    return () => {
      ipcRenderer.removeListener("global-recording-started", listener);
    };
  },
  onGlobalRecordingStopped: (callback: () => void) => {
    const listener = () => callback();
    ipcRenderer.on("global-recording-stopped", listener);
    return () => {
      ipcRenderer.removeListener("global-recording-stopped", listener);
    };
  },
  onTranscriptionProgress: (callback: (progress: unknown) => void) => {
    ipcRenderer.on("transcription-progress", (_event, progress) => callback(progress));
  },
  onTranscriptionComplete: (callback: (text: string) => void) => {
    ipcRenderer.on("transcription-complete", (_event, text) => callback(text));
  },
  onAudioChunk: (callback: (chunk: ArrayBuffer) => void) => {
    ipcRenderer.on("audio-chunk", (_event, chunk) => callback(chunk));
  },
  onOverlayStateChange: (callback: (state: unknown) => void) => {
    const listener = (_event: unknown, state: unknown) => callback(state);
    ipcRenderer.on("overlay-state-update", listener);
    // Return cleanup function
    return () => {
      ipcRenderer.removeListener("overlay-state-update", listener);
    };
  },
  onStreamingTranscript: (callback: (result: unknown) => void) => {
    const listener = (_event: unknown, result: unknown) => callback(result);
    ipcRenderer.on("streaming-transcript", listener);
    return () => {
      ipcRenderer.removeListener("streaming-transcript", listener);
    };
  },
  onStreamingError: (callback: (error: string) => void) => {
    const listener = (_event: unknown, error: string) => callback(error);
    ipcRenderer.on("streaming-error", listener);
    return () => {
      ipcRenderer.removeListener("streaming-error", listener);
    };
  },

  // Cleanup methods
  removeListener: (channel: string) => {
    ipcRenderer.removeAllListeners(channel);
  },
  removeAllListeners: (channel: string) => {
    ipcRenderer.removeAllListeners(channel);
  },
});

import { clipboard } from "electron";
import { execFile } from "child_process";
import { promisify } from "util";

const execFileAsync = promisify(execFile);

const isMac = process.platform === "darwin";

/**
 * Delay helper function
 */
const delay = (ms: number): Promise<void> => {
  return new Promise((resolve) => setTimeout(resolve, ms));
};

export class TextInjectionService {
  /**
   * Inject text into the currently focused input field
   * Uses clipboard + AppleScript Cmd+V simulation on macOS
   *
   * @param text - The text to inject
   * @param preserveClipboard - Whether to restore the original clipboard content after injection
   */
  async injectText(text: string, preserveClipboard = true): Promise<void> {
    if (!isMac) {
      throw new Error("Text injection is currently only supported on macOS");
    }

    if (!text || text.trim().length === 0) {
      throw new Error("Cannot inject empty text");
    }

    let originalClipboard: string | null = null;

    try {
      // Step 1: Store original clipboard content if preserving
      if (preserveClipboard) {
        try {
          originalClipboard = clipboard.readText();
        } catch (error) {
          console.warn("Failed to read clipboard:", error);
          // Continue anyway, just won't preserve
        }
      }

      // Step 2: Write text to clipboard
      clipboard.writeText(text);
      console.log("Text written to clipboard");

      // Small delay to ensure clipboard is updated
      await delay(50);

      // Step 3: Simulate Cmd+V via AppleScript
      // Using execFile for security (prevents shell injection)
      await execFileAsync("osascript", [
        "-e",
        'tell application "System Events" to keystroke "v" using command down',
      ]);
      console.log("Paste command executed");

      // Delay to ensure paste completes
      await delay(150);

    } finally {
      // Step 4: Restore original clipboard if requested
      if (preserveClipboard && originalClipboard !== null) {
        // Wait a bit longer to ensure paste has completed
        await delay(250);

        try {
          clipboard.writeText(originalClipboard);
          console.log("Original clipboard restored");
        } catch (error) {
          console.error("Failed to restore clipboard:", error);
          // Non-fatal error, text was still injected
        }
      }
    }
  }

  /**
   * Copy text to clipboard without pasting
   * Useful as a fallback when no input field is focused
   */
  async copyToClipboard(text: string): Promise<void> {
    if (!text || text.trim().length === 0) {
      throw new Error("Cannot copy empty text");
    }

    clipboard.writeText(text);
    console.log("Text copied to clipboard");
  }

  /**
   * Check if text injection is available on this platform
   */
  isAvailable(): boolean {
    return isMac;
  }

  /**
   * Get a user-friendly error message for unsupported platforms
   */
  getUnsupportedMessage(): string {
    if (!isMac) {
      return "Text injection is currently only supported on macOS. The transcription will be copied to your clipboard instead.";
    }
    return "";
  }
}

// Singleton instance
let textInjectionServiceInstance: TextInjectionService | null = null;

export function getTextInjectionService(): TextInjectionService {
  if (!textInjectionServiceInstance) {
    textInjectionServiceInstance = new TextInjectionService();
  }
  return textInjectionServiceInstance;
}
